var http = require("http");

var commandOptions =  {
	path: "execute",
	method: "POST"
}

exports.command = function(script, callback) 
{
	var request = this.createRequest(this.createOptions(commandOptions), this.proxyResponse(callback));
	var data = JSON.stringify( {"script": script, args: []} );
	request.write(data);
	request.end();
};