var http = require("http");

var commandOptions =  {
	path: "window",
	method: "POST"
}

exports.command = function(windowHandle, callback) 
{
	var data = {"name": windowHandle};
		
	this.executeProtocolCommand(
		this.createOptions(commandOptions), 
		this.proxyResponseNoReturn(callback), 
		data
	);
};